mapper.select:
	SELECT CASE WHEN TRUNC((SYSDATE - E.SEND_DATE) * 24 * 60 * 60) < 180 THEN 1 ELSE 0 END timegap
		  FROM (
	            SELECT MEMBER_NO, MAX(SEND_DATE) SEND_DATE
	            FROM Email_Auth_Hist
	            WHERE EMAIL = #{email}
	            AND RANDOM_NO = #{findPwdCode}
	            GROUP BY MEMBER_NO
        		) E
		 INNER JOIN MEMBER M
			ON M.MEMBER_NO = E.MEMBER_NO
        where M.member_id = #{memberId}

mapper.update:
UPDATE MEMBER SET
	   MEMBER_PWD=#{memberPwd}
	   WHERE MEMBER_ID=#{memberId}
